import { HttpClient } from '@angular/common/http';

export class APIClient {

  protected apiRoot = '';

  constructor(protected http: HttpClient) { }

  public post<T>(method: string, args?: any): Promise<T> {
    return this.http.post<T>(this.createMethodUrl(method), args).toPromise();
  }

  public put<T>(method: string, args: any): Promise<T> {
    return this.http.put<T>(this.createMethodUrl(method), args).toPromise();
  }

  public get<T>(method: string = '', args?: any): Promise<T> {
    return this.http.get<T>(this.createMethodUrl(method), { params: args, observe: 'body', responseType: 'json' }).toPromise();
  }

  public getBlob(method: string, args?: any): Promise<Blob> {
    return this.http.get(this.createMethodUrl(method), { params: args, observe: 'body', responseType: 'blob' }).toPromise();
  }

  public delete<T>(method: string, args?: any): Promise<T> {
    return this.http.delete<T>(this.createMethodUrl(method), { params: args, observe: 'body' }).toPromise();
  }

  public patch<T>(method: string, args: any): Promise<T> {
    return this.http.patch<T>(this.createMethodUrl(method), args).toPromise();
  }

  protected createMethodUrl(method: string): string {
    return method ? (this.apiRoot + '/' + method) : this.apiRoot;
  }
}
