import { lastValueFrom } from 'rxjs';
export class APIClient {
  constructor(http) {
    this.http = http;
    this.apiRoot = '';
  }
  post(method, args) {
    return lastValueFrom(this.http.post(this.createMethodUrl(method), args));
  }
  put(method, args) {
    return lastValueFrom(this.http.put(this.createMethodUrl(method), args));
  }
  get(method = '', args) {
    return lastValueFrom(this.http.get(this.createMethodUrl(method), {
      params: args,
      observe: 'body',
      responseType: 'json'
    }));
  }
  getBlob(method, args) {
    return lastValueFrom(this.http.get(this.createMethodUrl(method), {
      params: args,
      observe: 'body',
      responseType: 'blob'
    }));
  }
  delete(method, args) {
    return lastValueFrom(this.http.delete(this.createMethodUrl(method), {
      params: args,
      observe: 'body'
    }));
  }
  patch(method, args) {
    return lastValueFrom(this.http.patch(this.createMethodUrl(method), args));
  }
  createMethodUrl(method) {
    return method ? this.apiRoot + '/' + method : this.apiRoot;
  }
}
